mapper.select:
SELECT * FROM machine
  where 1=1

      and id=#{id}


      and machine_id=#{machine_id}


      and order_id=#{order_id}


      and nameplate=#{nameplate}


      and location=#{location}


      and status=#{status}


      and machine_type=#{machine_type}



      and DATE_FORMAT(create_time,'%Y-%m-%d %T') >= #{query_start_time}


      and DATE_FORMAT(create_time,'%Y-%m-%d %T') <= #{query_finish_time}

  ORDER By create_time, ship_time DESC

SELECT * FROM machine
where 1=1

    and id=#{id}


    and machine_id like CONCAT('%','${machine_id}','%' )


    and order_id like CONCAT('%','${order_id}','%' )


    and nameplate like CONCAT('%','${nameplate}','%' )


    and location=#{location}


    and status=#{status}


    and machine_type=#{machine_type}



    and DATE_FORMAT(create_time,'%Y-%m-%d %T') >= #{query_start_time}


    and DATE_FORMAT(create_time,'%Y-%m-%d %T') <= #{query_finish_time}

ORDER By create_time, ship_time DESC

SELECT *, mo.order_num FROM machine m
  JOIN machine_order mo ON m.order_id = mo.id
  JOIN process_record pr ON m.id = pr.machine_id
  JOIN task_record tr ON pr.id = tr.process_record_id
  JOIN task_plan tp ON tr.id = tp.task_record_id
  WHERE m.status > 0 AND m.status < 6

      and id=#{id}


      and machine_id=#{machine_id}


      and order_id=#{order_id}


      and nameplate=#{nameplate}


      and location=#{location}


      and status=#{status}


      and machine_type=#{machine_type}



      and DATE_FORMAT(create_time,'%Y-%m-%d %T') >= #{query_start_time}


      and DATE_FORMAT(create_time,'%Y-%m-%d %T') <= #{query_finish_time}

  ORDER By create_time, ship_time DESC

SELECT * FROM machine
where 1=1

    and id=#{id}


    and machine_id like CONCAT('%','${machine_id}','%' )


    and order_id like CONCAT('%','${order_id}','%' )


    and nameplate like CONCAT('%','${nameplate}','%' )


    and location=#{location}


    and status=#{status}


    and machine_type=#{machine_type}



    and DATE_FORMAT(create_time,'%Y-%m-%d %T') >= #{query_start_time}


    and DATE_FORMAT(create_time,'%Y-%m-%d %T') <= #{query_finish_time}

ORDER By create_time, ship_time DESC
