mapper.select:
SELECT * FROM machine
where 1=1

    and id=#{id}


    and machine_strid=#{machine_strid}


    and order_id=#{order_id}


    and nameplate=#{nameplate}


    and location=#{location}


    and status=#{status}


    and machine_type=#{machine_type}



    and DATE_FORMAT(create_time,'%Y-%m-%d %T') >= #{query_start_time}


    and DATE_FORMAT(create_time,'%Y-%m-%d %T') <= #{query_finish_time}

ORDER By create_time, ship_time DESC,ship_time DESC

SELECT * FROM machine
where 1=1

    and id=#{id}


    and machine_strid like CONCAT('%','${machine_strid}','%' )


    and order_id like CONCAT('%','${order_id}','%' )


    and nameplate like CONCAT('%','${nameplate}','%' )


    and location=#{location}


    and status=#{status}


    and machine_type=#{machine_type}



    and DATE_FORMAT(create_time,'%Y-%m-%d %T') >= #{query_start_time}


    and DATE_FORMAT(create_time,'%Y-%m-%d %T') <= #{query_finish_time}

ORDER By create_time DESC,update_time DESC, ship_time DESC

SELECT * FROM (
    SELECT m.*, mo.order_num, mo.plan_ship_date, pr.id AS process_record_id, mo.contract_ship_date, COUNT(tr.id) AS
    total_task_num, COUNT(tp.id) AS planed_task_num FROM machine m
    JOIN machine_order mo ON m.order_id = mo.id
    JOIN process_record pr ON m.id = pr.machine_id
    JOIN task_record tr ON pr.id = tr.process_record_id
    LEFT JOIN task_plan tp ON tr.id = tp.task_record_id
    WHERE m.status > 0 AND m.status < 7

        and machine_strid=#{machine_strid}


        and mo.order_num=#{order_num}


        and m.nameplate=#{nameplate}


        and m.location=#{location}


        and m.status=#{status}


        and m.machine_type=#{machine_type}





            and DATE_FORMAT(mo.contract_ship_date,'%Y-%m-%d') >= #{query_start_time}


            and DATE_FORMAT(mo.contract_ship_date,'%Y-%m-%d') <= #{query_finish_time}





            and DATE_FORMAT(mo.plan_ship_date,'%Y-%m-%d') >= #{query_start_time}


            and DATE_FORMAT(mo.plan_ship_date,'%Y-%m-%d') <= #{query_finish_time}


    GROUP BY m.id
    ORDER By m.create_time DESC,m.update_time DESC, m.ship_time DESC ) AS v
WHERE v.total_task_num > v.planed_task_num

SELECT * FROM (
    SELECT m.*, mo.order_num, mo.plan_ship_date, pr.id AS process_record_id, mo.contract_ship_date, COUNT(tr.id) AS
    total_task_num, COUNT(tp.id) AS planed_task_num FROM machine m
    JOIN machine_order mo ON m.order_id = mo.id
    JOIN process_record pr ON m.id = pr.machine_id
    JOIN task_record tr ON pr.id = tr.process_record_id
    LEFT JOIN task_plan tp ON tr.id = tp.task_record_id
    WHERE m.status > 0 AND m.status < 7

        and m.machine_strid like CONCAT('%','${machine_strid}','%' )


        and mo.order_num like CONCAT('%','${order_num}','%' )


        and m.nameplate like CONCAT('%','${nameplate}','%' )


        and m.location=#{location}


        and m.status=#{status}


        and m.machine_type=#{machine_type}





            and DATE_FORMAT(mo.contract_ship_date,'%Y-%m-%d') >= #{query_start_time}


            and DATE_FORMAT(mo.contract_ship_date,'%Y-%m-%d') <= #{query_finish_time}





            and DATE_FORMAT(mo.plan_ship_date,'%Y-%m-%d') >= #{query_start_time}


            and DATE_FORMAT(mo.plan_ship_date,'%Y-%m-%d') <= #{query_finish_time}



    GROUP BY m.id
    ORDER By m.create_time DESC,m.update_time DESC,m.ship_time DESC
) AS v WHERE v.total_task_num > v.planed_task_num

SELECT * FROM
(SELECT m.*,mo.contract_id,mo.order_num,mo.contract_ship_date,c.contract_num,pr.id as
process_record_id,pr.node_data,pr.link_data,pr.process_id
FROM machine m
LEFT JOIN machine_order mo ON m.order_id=mo.id
LEFT JOIN contract c ON mo.contract_id=c.id
LEFT JOIN process_record pr ON m.id=pr.machine_id) V
WHERE 1=1


    and ISNULL(process_record_id)



    and process_record_id!=""


    and order_num=#{order_num}


    and contract_num=#{contract_num}


    and machine_strid=#{machine_strid}


    and order_id=#{order_id}


    and nameplate=#{nameplate}


    and machine_type = #{machineType}


    and location=#{location}


    and status=#{status}



    and DATE_FORMAT(create_time,'%Y-%m-%d') >= #{query_start_time}


    and DATE_FORMAT(create_time,'%Y-%m-%d') <= #{query_finish_time}

ORDER By create_time DESC, update_time DESC,ship_time DESC

SELECT * FROM (SELECT m.*,mo.contract_id,mo.order_num,mo.contract_ship_date,c.contract_num,pr.id as process_record_id,pr.node_data,pr.link_data,pr.process_id FROM machine m LEFT JOIN machine_order mo ON m.order_id=mo.id LEFT JOIN contract c ON mo.contract_id=c.id LEFT JOIN process_record pr ON m.id=pr.machine_id) V WHERE 1=1
and ISNULL(process_record_id)
and process_record_id!=""
and order_id like #{order_id}
and order_num like CONCAT('%','${order_num}','%' )
and contract_num like CONCAT('%','${contract_num}','%' )
and machine_strid like CONCAT('%','${machine_strid}','%' )
and nameplate like CONCAT('%','${nameplate}','%' )
and machine_type = #{machineType}
and location=#{location}
and status=#{status}
and DATE_FORMAT(create_time,'%Y-%m-%d') >= #{query_start_time}
and DATE_FORMAT(create_time,'%Y-%m-%d') <= #{query_finish_time}
ORDER By create_time DESC, update_time DESC,ship_time DESC

SELECT * FROM (SELECT m.*,mo.contract_id,mo.order_num,mo.contract_ship_date,mo.plan_ship_date,c.contract_num,pr.id as process_record_id,pr.node_data,pr.link_data,pr.process_id, pr.create_time as process_create_time,pr.end_time as process_end_time FROM machine m LEFT JOIN machine_order mo ON m.order_id=mo.id LEFT JOIN contract c ON mo.contract_id=c.id LEFT JOIN process_record pr ON m.id=pr.machine_id) V WHERE process_record_id!=""
and order_id like #{order_id}
and order_num like CONCAT('%','${order_num}','%' )
and contract_num like CONCAT('%','${contract_num}','%' )
and machine_strid like CONCAT('%','${machine_strid}','%' )
and nameplate like CONCAT('%','${nameplate}','%' )
and location=#{location}
and status=#{status}
and DATE_FORMAT(process_end_time,'%Y-%m-%d') >= #{query_start_time}
and DATE_FORMAT(process_end_time,'%Y-%m-%d') <= #{query_finish_time}
ORDER By process_create_time DESC, update_time DESC,create_time DESC

SELECT * FROM
(SELECT m.*,mo.contract_id,mo.order_num,mo.contract_ship_date,mo.plan_ship_date,c.contract_num,pr.id as
process_record_id,pr.node_data,pr.link_data,pr.process_id,
pr.create_time as process_create_time,pr.end_time as process_end_time
FROM machine m
LEFT JOIN machine_order mo ON m.order_id=mo.id
LEFT JOIN contract c ON mo.contract_id=c.id
LEFT JOIN process_record pr ON m.id=pr.machine_id) V
WHERE process_record_id!=""

    and order_num=#{order_num}


    and contract_num=#{contract_num}


    and machine_strid=#{machine_strid}


    and order_id=#{order_id}


    and nameplate=#{nameplate}


    and location=#{location}


    and status=#{status}



    and DATE_FORMAT(process_end_time,'%Y-%m-%d') >= #{query_start_time}


    and DATE_FORMAT(process_end_time,'%Y-%m-%d') <= #{query_finish_time}

ORDER By process_create_time DESC, update_time DESC,create_time DESC
