mapper.select:
select mo.*, od.id detail_id, od.special_towel_color, od.special_towel_daxle, od.special_towel_haxle, od.special_towel_motor, od.special_taping_head, od.special_towel_needle, od.electric_pc,od.electric_language, od.electric_motor, od.electric_motor_xy, od.electric_trim, od.electric_power, od.electric_switch,od.color_change_mode, od.electric_oil, od.axle_split, od.axle_panel, od.axle_needle,od.axle_needle_type, od.axle_rail, od.axle_down_check, od.axle_hook, od.axle_jump, od.axle_upper_thread, od.axle_addition, od.framework_color, od.framework_platen, od.framework_platen_color, od.framework_ring, od.framework_bracket, od.framework_stop, od.framework_light, od.driver_type, od.driver_method, od.driver_reel_hole, od.driver_horizon_num, od.driver_vertical_num, od.driver_reel, os.id order_sign_id, os.order_id,os.sign_content,os.create_time os_create_time, os.update_time os_update_time,os.current_step, mt.id machineTypeID, mt.`name`, con.contract_num, con.customer_name as customer, oll.id as order_loading_list_id,oll.file_name,oll.type as file_type, osr.id splitRecordId, osr.user_id split_user_id, osr.order_id split_order_id, osr.split_reason, ocr.id changeRecordId, ocr.user_id change_user_id, ocr.order_id change_order_id, ocr.change_reason from machine_order mo LEFT JOIN order_sign os on mo.id = os.order_id LEFT JOIN order_detail od on mo.order_detail_id=od.id LEFT JOIN machine_type mt on mo.machine_type=mt.id LEFT JOIN contract con on mo.contract_id = con.id LEFT JOIN order_loading_list oll ON oll.order_id=mo.id LEFT JOIN order_change_record ocr on ocr.order_id = mo.original_order_id LEFT JOIN order_split_record osr on osr.order_id = mo.original_order_id where 1=1 AND mo.valid!=0
and mo.id=#{id}
and mo.contract_id=#{contract_id}
and mo.order_num like CONCAT('%','${order_num}','%' )
and con.contract_num like CONCAT('%','${contract_num}','%' )
and con.market_group_name like CONCAT('%','${market_group_name}','%' )
and mo.status=${status}
and mo.sellman like CONCAT('%','${sellman}','%' )
and con.customer_name like CONCAT('%','${customer}','%' )
and DATE_FORMAT(mo.create_time,'%Y-%m-%d') >= #{query_start_time}
and DATE_FORMAT(mo.create_time,'%Y-%m-%d') <= #{query_finish_time}
and mt.name like CONCAT('%','${machine_name}','%' )
GROUP BY mo.id ORDER By mo.create_time DESC,mo.update_time DESC,os_update_time DESC

select mo.*, od.id detail_id, od.special_towel_color, od.special_towel_daxle, od.special_towel_haxle, od.special_towel_motor, od.special_taping_head, od.special_towel_needle, od.electric_pc,od.electric_language, od.electric_motor, od.electric_motor_xy, od.electric_trim, od.electric_power, od.electric_switch,od.color_change_mode, od.electric_oil, od.axle_split, od.axle_panel, od.axle_needle,od.axle_needle_type,od.axle_rail, od.axle_down_check, od.axle_hook, od.axle_jump, od.axle_upper_thread, od.axle_addition, od.framework_color, od.framework_platen, od.framework_platen_color, od.framework_ring, od.framework_bracket, od.framework_stop, od.framework_light, od.driver_type, od.driver_method, od.driver_reel_hole, od.driver_horizon_num, od.driver_vertical_num, od.driver_reel, os.id order_sign_id, os.order_id,os.sign_content,os.create_time os_create_time, os.update_time os_update_time,os.current_step, mt.id machineTypeID, mt.`name`, con.contract_num, con.customer_name as customer, oll.id as order_loading_list_id,oll.file_name,oll.type as file_type, osr.id splitRecordId, osr.user_id split_user_id, osr.order_id split_order_id, osr.split_reason, ocr.id changeRecordId, ocr.user_id change_user_id, ocr.order_id change_order_id, ocr.change_reason from machine_order mo LEFT JOIN order_sign os on mo.id = os.order_id LEFT JOIN order_detail od on mo.order_detail_id=od.id LEFT JOIN machine_type mt on mo.machine_type=mt.id LEFT JOIN contract con on mo.contract_id = con.id LEFT JOIN order_loading_list oll ON oll.order_id=mo.id LEFT JOIN order_change_record ocr on ocr.order_id = mo.original_order_id LEFT JOIN order_split_record osr on osr.order_id = mo.original_order_id where 1=1 AND mo.valid!=0
and mo.id=#{id}
and mo.contract_id=#{contract_id}
and mo.order_num like CONCAT('%','${order_num}','%' )
and con.contract_num = CONCAT('%','${contract_num}','%' )
and con.market_group_name like CONCAT('%','${market_group_name}','%' )
and mo.status=${status}
and mo.sellman = CONCAT('%','${sellman}','%' )
and con.customer_name = CONCAT('%','${customer}','%' )
and DATE_FORMAT(mo.create_time,'%Y-%m-%d') >= #{query_start_time}
and DATE_FORMAT(mo.create_time,'%Y-%m-%d') <= #{query_finish_time}
and mt.name = CONCAT('%','${machine_name}','%' )
GROUP BY mo.id ORDER By mo.create_time DESC,mo.update_time DESC,os_update_time DESC

SELECT * from order_loading_list oll
join machine_order mo on mo.id=oll.order_id
WHERE oll.id = #{ollId};
